INSERT INTO `news` VALUES ('10001', '许凯呼吁粉丝不要追星:别浪费钱买礼物写信就行', '娱乐', '2019-11-27 00:00:00', '许凯节目中叫粉丝不要追星，“不要浪费钱买礼物，写写信就可以。”网友大呼暖心宠粉。', '北京日报', '20');
